import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(){
    super();
    this.state = {
      todos: [],
    };
  }

  fetchFunc = () => {

    fetch('https://api.randomuser.me'
    )
    .then(response => response.json())
    .then(data => {
      this.setState({todos: data.results})
    })
    console.log('Cheguei no compnentDidMount');
  }

  componentDidMount(){
    this.fetchFunc();
    console.log('componentDidMount: Bem Vindo!!');
  }
  render(){
    const { todos } = this.state;
   /* console.log(todos.map(elemento => elemento.email) );*/ // Confrimando se esta tudo correto
  return (
    <div>
      <div>
        {todos.map((dados, index) => {
          return(
          <div key = {index}>
            Name 
            {`${dados.name.title} ${dados.name.first} ${dados.name.last}`}
            </div>)
        })

        }
      </div>

    </div>
  )
}
}

export default App;
